import subprocess
import json


class Executor:
    def execute_task(self, task_data):
        """
        Executes a task based on its type.
        This is a simplified executor. A real implementation would be more
        robust, sandboxed, and support different environments.
        """
        task_type = task_data.get('type')
        if task_type == 'execute_script':
            script_path = task_data.get('script_path')
            try:
                result = subprocess.run(['python', script_path],
                                        capture_output=True, text=True,
                                        check=True)
                return {"stdout": result.stdout, "stderr": result.stderr}
            except subprocess.CalledProcessError as e:
                return {"error": str(e), "stdout": e.stdout,
                        "stderr": e.stderr}
            except FileNotFoundError:
                return {"error": f"Script not found at {script_path}"}
        elif task_type == 'data_processing':
            # Example of another task type
            data = task_data.get('data')
            # Process the data in some way
            processed_data = {"processed_length": len(data)}
            return {"result": processed_data}
        else:
            return {"error": f"Unsupported task type: {task_type}"}

