class NexaPodClient:
    def __init__(self, server_url="http://127.0.0.1:5000"):
        self.server_url = server_url
// ...existing code...
        self.reputation_manager = ReputationManager()
        self.tier_manager = TierManager()

    def create_profile(self, username):
        if self.profiles.get_profile(username):
            raise ValueError(f"Profile for '{username}' already exists.")
        private_key, public_key = self.key_manager.generate_keys()
        self.profiles.create_profile(username, public_key)
        # In a real app, the private key should be handled more securely
        # (e.g., encrypted storage)
        print(f"Generated private key for {username}: {private_key}")
        self.load_profile(username, private_key)
        return private_key

    def load_profile(self, username, private_key):
        profile_data = self.profiles.get_profile(username)
        if not profile_data:
            raise ValueError(f"No profile found for '{username}'.")
        # In a real app, you'd verify the private key matches the public key
        self.profile = {"username": username, "private_key": private_key,
                        "public_key": profile_data['public_key']}
        print(f"Profile for {username} loaded.")

    def submit_task(self, task_type, task_data):
        if not self.profile:
            raise ConnectionError("No profile loaded. Please create or load "
                                  "a profile first.")

        task = {
            "id": str(uuid.uuid4()),
            "type": task_type,
            "data": task_data,
            "submitter": self.profile['username'],
            "timestamp": time.time(),
            "status": "pending"
        }
        # Sign the task to prove identity
        signature = self.key_manager.sign(self.profile['private_key'],
                                          json.dumps(task, sort_keys=True))

        payload = {"task": task, "signature": signature}

        try:
            response = requests.post(f"{self.server_url}/submit_task",
                                     json=payload)
            response.raise_for_status()
            self.ledger.add_transaction(
                Transaction("submit_task", payload, self.key_manager))
            return task['id']
        except requests.exceptions.RequestException as e:
            print(f"Error submitting task to server: {e}")
            raise

    def get_reputation(self):
        if not self.profile:
            return 0
        return self.reputation_manager.get_reputation(
            self.profile['username'])

    def get_completed_tasks(self):
        # This would fetch from a local DB or the network
        return []

    def get_all_tasks(self):
        # Placeholder to get tasks from the ledger/local state
        return {}

    def get_tier(self):
        if not self.profile:
            return "N/A"
        reputation = self.get_reputation()
        return self.tier_manager.get_tier(reputation)

import time
import requests


class CoordinatorClient:
    """Client for interacting with the coordinator API."""
    def __init__(self, config: dict):
        self.url = config['coordinator_url']
        self.node_id = config.get('node_id')
        self.poll_interval = config.get('poll_interval', 10)

    def register_node(self, profile: dict):
        """Register this node with the coordinator."""
        resp = requests.post(f"{self.url}/register", json=profile)
        if resp.ok:
            self.node_id = resp.json().get('node_id')
        else:
            raise Exception(f"Registration failed: {resp.text}")

    def poll_job(self) -> dict:
        """Poll coordinator for a new job."""
        resp = requests.get(
            f"{self.url}/job",
            params={'node_id': self.node_id}
        )
        if resp.ok and resp.json():
            return resp.json()
        time.sleep(self.poll_interval)
        return None

    def submit_result(self, result: dict) -> dict:
        """Submit execution result back to coordinator."""
        resp = requests.post(f"{self.url}/result", json=result)
        if resp.ok:
            return resp.json()
        raise Exception(f"Result submission failed: {resp.text}")

    def submit_job(self, param: dict) -> dict:
        """Submit a new job to the coordinator."""
        resp = requests.post(f"{self.url}/jobs", json=param)
        if not resp.ok:
            raise Exception(f"Job submission failed: {resp.text}")
        return resp.json()

    def get_status(self) -> dict:
        """Check node status with coordinator."""
        resp = requests.get(
            f"{self.url}/status",
            params={'node_id': self.node_id}
        )
        if resp.ok:
            return resp.json()
        raise Exception(f"Status check failed: {resp.text}")


def get_node_profile() -> dict:
    """Simulate a static node profile for testing."""
    return {
        'cpu': 'Intel Xeon',
        'ram_gb': 32,
        'cores': 16,
        'node_id': 'testnode'
    }


def execute_job(job: dict) -> dict:
    """Simulate job execution for testing."""
    print(f"Executing job {job['job_id']} with image {job['docker_image']}")
    time.sleep(2)
    return {
        'status': 'completed',
        'output': f"Output of job {job['job_id']}"
    }


def log_result(result: dict):
    """Simulate logging of a job result."""
