"""
REST API for NEXAPod coordinator.
"""

import os
from flask import Flask, request, jsonify
from .node import Node
from .tier import Tier
from .scheduler import Scheduler
from .database import Database

app = Flask(__name__)
scheduler = Scheduler()
db = Database()


@app.route('/register', methods=['POST'])
def register():
    """Register a new node with its profile and tier."""
    data = request.get_json()
    node = Node(data.get('id'), Tier(data.get('tier')))
    db.store_node({
        "id": node.id,
        "tier": node.tier.value,
        "profile": node.profile
    })
    return jsonify({"status": "registered", "node": node.id})


@app.route('/submit-job', methods=['POST'])
def submit_job():
    """Accept a job submission and enqueue it for scheduling."""
    job = request.get_json()
    scheduler.submit_job(job)
    return jsonify({"status": "job submitted", "job_id": job.get('id')})


@app.route('/status', methods=['GET'])
def status():
    """Return current nodes and jobs stored in the database."""
    return jsonify({
        "nodes": db.get_nodes(),
        "jobs": db.get_jobs()
    })


if __name__ == '__main__':
    host = os.getenv('HOST', '0.0.0.0')
    port = int(os.getenv('PORT', 8000))
    app.run(host=host, port=port)

"""
REST API for NEXAPod coordinator.
"""

import os
import json
from flask import Flask, request, jsonify
from .scheduler import Scheduler
from .database import Database

app = Flask(__name__)
scheduler = Scheduler()
db = Database()


@app.route('/register', methods=['POST'])
def register():
    """Register a new node with its profile."""
    data = request.get_json()
    if not data or 'node_id' not in data or 'profile' not in data:
        return jsonify({"error": "Invalid registration data"}), 400

    node_id = data['node_id']
    profile = data['profile']

    db.store_node(node_id, json.dumps(profile))
    return jsonify({"status": "registered", "node_id": node_id})


@app.route('/job', methods=['GET'])
def get_job():
    """A node requests a job from the scheduler."""
    node_id = request.headers.get('X-Node-ID')
    if not node_id:
        return jsonify({"error": "X-Node-ID header is required"}), 400

    job = scheduler.get_job(node_id)
    if job:
        db.store_job(job)
        return jsonify(job)

    return jsonify({"status": "no_job_available"})


@app.route('/result', methods=['POST'])
def submit_result():
    """A node submits the result of a completed job."""
    data = request.get_json()
    if not data or 'job_id' not in data or 'result' not in data:
        return jsonify({"error": "Invalid result submission"}), 400

    db.update_job_result(data['job_id'], json.dumps(data['result']))
    return jsonify({"status": "result_received", "job_id": data['job_id']})


@app.route('/nodes', methods=['GET'])
def get_nodes():
    """Return a list of all registered nodes."""
    nodes = db.get_nodes()
    return jsonify(nodes)


@app.route('/jobs', methods=['GET'])
def get_jobs():
    """Return a list of all jobs."""
    jobs = db.get_jobs()
    return jsonify(jobs)


if __name__ == '__main__':
    host = os.getenv('HOST', '0.0.0.0')
    port = int(os.getenv('PORT', 8000))
    app.run(host=host, port=port)

""" Database access layer for NEXAPod coordinator. """
import sqlite3
class Database: """Handles persistence of nodes, jobs, and logs.""" def __init__(self, path: str = "nexapod.db"): self.conn = sqlite3.connect(path, check_same_thread=False) self.create_tables()
def create_tables(self): """Create tables for nodes, jobs, and logs if they do not exist.""" cursor = self.conn.cursor() cursor.execute( """CREATE TABLE IF NOT EXISTS nodes ( id TEXT PRIMARY KEY, tier TEXT, profile TEXT)""" ) cursor.execute( """CREATE TABLE IF NOT EXISTS jobs ( id TEXT PRIMARY KEY, data TEXT, result TEXT)""" ) cursor.execute( """CREATE TABLE IF NOT EXISTS logs ( id INTEGER PRIMARY KEY AUTOINCREMENT, job_id TEXT, log TEXT)""" ) self.conn.commit()
def store_node(self, node: dict): """Insert or update a node record.""" cursor = self.conn.cursor() cursor.execute( "INSERT OR REPLACE INTO nodes VALUES (?,?,?)", (node["id"], node["tier"], str(node["profile"])) ) self.conn.commit()
def store_job(self, job: dict, result: dict): """Insert or update a job record with its result.""" cursor = self.conn.cursor() cursor.execute( "INSERT OR REPLACE INTO jobs VALUES (?,?,?)", (job["id"], str(job), str(result)) ) self.conn.commit()
def get_nodes(self) -> list: """Retrieve all stored nodes.""" cursor = self.conn.cursor() cursor.execute("SELECT * FROM nodes") return cursor.fetchall()
def get_jobs(self) -> list: """Retrieve all stored jobs.""" cursor = self.conn.cursor() cursor.execute("SELECT * FROM jobs") return cursor.fetchall()

""" Database access layer for NEXAPod coordinator. """
import sqlite3 import json
class Database: """Handles persistence of nodes, jobs, and logs.""" def __init__(self, path: str = "nexapod.db"): self.conn = sqlite3.connect(path, check_same_thread=False) self.create_tables()
def create_tables(self): """Create tables for nodes, jobs, and logs if they do not exist.""" cursor = self.conn.cursor() cursor.execute( """CREATE TABLE IF NOT EXISTS nodes ( id TEXT PRIMARY KEY, profile TEXT)""" ) cursor.execute( """CREATE TABLE IF NOT EXISTS jobs ( job_id TEXT PRIMARY KEY, status TEXT, assigned_to TEXT, data TEXT, result TEXT)""" ) self.conn.commit()
def store_node(self, node_id: str, profile: str): """Insert or update a node record.""" cursor = self.conn.cursor() cursor.execute( "INSERT OR REPLACE INTO nodes (id, profile) VALUES (?,?)", (node_id, profile) ) self.conn.commit()
def store_job(self, job: dict): """Insert or update a job record.""" cursor = self.conn.cursor() cursor.execute( "INSERT OR REPLACE INTO jobs (job_id, status, assigned_to, data, result) VALUES (?,?,?,?,?)", (job["job_id"], job["status"], job["assigned_to"], json.dumps(job.get("data")), json.dumps(job.get("result"))) ) self.conn.commit()
def update_job_result(self, job_id: str, result: str): """Update a job with its result and set status to completed.""" cursor = self.conn.cursor() cursor.execute( "UPDATE jobs SET result = ?, status = 'completed' WHERE job_id = ?", (result, job_id) ) self.conn.commit()
def get_nodes(self) -> list: """Retrieve all stored nodes.""" cursor = self.conn.cursor() cursor.execute("SELECT id, profile FROM nodes") # Return a list of dicts for easier JSON serialization return [{"id": row[0], "profile": json.loads(row[1])} for row in cursor.fetchall()]
def get_jobs(self) -> list: """Retrieve all stored jobs.""" cursor = self.conn.cursor() cursor.execute("SELECT job_id, status, assigned_to, data, result FROM jobs") # Return a list of dicts return [ { "job_id": row[0], "status": row[1], "assigned_to": row[2], "data": json.loads(row[3]) if row[3] else None, "result": json.loads(row[4]) if row[4] else None, } for row in cursor.fetchall() ]

